import React, { useState, useEffect } from 'react';
import FeedbackForm from './components/FeedbackForm';
import FeedbackList from './components/FeedbackList';
import { fetchFeedback, submitFeedback } from './api';

function App() {
  const [feedback, setFeedback] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRating, setSelectedRating] = useState(null);

  useEffect(() => {
    fetchFeedback()
      .then(data => {
        setFeedback(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const handleSubmit = async (formData) => {
    try {
      const newFeedback = await submitFeedback(formData);
      setFeedback(prev => [...prev, newFeedback]);
    } catch (err) {
      setError(err.message);
    }
  };

  const filteredFeedback = selectedRating
    ? feedback.filter(f => f.rating === selectedRating)
    : feedback;

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="container mx-auto px-4">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">User Feedback System</h1>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
            {error}
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-2">
            <FeedbackForm onSubmit={handleSubmit} />
          </div>
          <div className="lg:col-span-1">
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Filter by Rating
              </label>
              <select
                value={selectedRating}
                onChange={(e) => setSelectedRating(Number(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="">All Ratings</option>
                {[1, 2, 3, 4, 5].map(rating => (
                  <option key={rating} value={rating}>
                    {rating} stars
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        <div className="mt-8">
          <FeedbackList feedback={filteredFeedback} loading={loading} />
        </div>
      </div>
    </div>
  );
}

export default App;
